use splc::*;

#[test]
fn splc_test1() {
    let fasta = r#">Rosalind_10
ATGGTCTACATAGCTGACAAACAGCACGTAGCAATCGGTCGAATCTCGAGAGGCATATGGTCACATGATCGGTCGAGCGTGTTTCAAAGTTTGCGCCTAG
>Rosalind_12
ATCGGTCGAA
>Rosalind_15
ATCGGTCGAGCGTGT"#;

    assert_eq!(splc(fasta), "MVYIADKQHVASREAYGHMFKVCA");
}

